export default class ElementObserver {
    constructor(observable: any, el: any);
    get observer(): any;
    get inView(): boolean;
    onIntersecting(isIntersecting: any): void;
    enterViewOnce(): Promise<any>;
    enterView(fn: any): void;
    leaveViewOnce(): Promise<any>;
    leaveView(fn: any): void;
    destroy(): void;
    #private;
}
